$(window).trigger('scroll');
$(window).on('scroll', function () {
  var pixels = 50;
  var top = 1200;
  var winScrollTop = $(window).scrollTop();
    var winHeight = $(window).height();
    var floaterHeight = $('#floater').outerHeight(true);
    var fromBottom = 5;
  var top02 = winScrollTop + winHeight - floaterHeight - fromBottom;
  $('#floater').css({ 'top': top02 + 'px' });
  /* floater 2*/
  var floaterHeight2 = $('#floater2').outerHeight(true);
  var fromBottom2 = 90;
  var top03 = winScrollTop + winHeight - floaterHeight2 - fromBottom2;
  $('#floater2').css({ 'top': top03 + 'px' });


  if ($(window).scrollTop() > pixels) {
    $('.navbar-expand-md').addClass('navbar-reduce');
    $('.navbar-expand-md').removeClass('navbar-trans');
    $('.nav-icon-img').addClass('hidden-logo');
    $('.nav-icon-img').removeClass('display-logo');
    $('.nav-icon-img').removeClass('animation-brand');
  } else {
    $('.navbar-expand-md').addClass('navbar-trans');
    $('.navbar-expand-md').removeClass('navbar-reduce');
    $('.nav-icon-img').removeClass('hidden-logo');
    $('.nav-icon-img').addClass('display-logo');
    $('.nav-icon-img').addClass('animation-brand');
  }
  if ($(window).scrollTop() > top) {
    $('.scrolltop-mf').fadeIn(1000, "easeInOutExpo");
  } else {
    $('.scrolltop-mf').fadeOut(1000, "easeInOutExpo");
  }
});
